import React, {Component} from 'react';
import AuthenticationService from "./AuthenticationService";
import {Route, Redirect} from "react-router-dom";

class AuthenticatedRoute extends Component {
    render() {
        if (AuthenticationService.isUserLoggedIn()) {
            return <Route {...this.props}/>
        } else {
            return <Redirect to="/login"/> // bencce şöyle yapalım ana menu oldun yada boş bir sayfa yap
            //çıkış yapınca o boş sayfaya gitsin orda beklesin birdaha girmek sitiyorsa login butonuna basar böylel,kle sayfa yenilenir.
            // peki deneyelim bakalım
        }
    }
}

export default AuthenticatedRoute;
