import React from 'react'
import {Component} from "react";
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';
import AuthenticationService from "./AuthenticationService";
class Login extends Component {
    constructor(props){
        super(props);
        this.state = {
            username: "test",
            password: "test",
            isLoggedIn: null
        };
    }
    render() {
        return (
                <MuiThemeProvider>
                    <div>
                        <AppBar
                            title="Login"
                        />
                        <TextField
                            hintText="Enter your Username"
                            floatingLabelText="Username"
                            onChange = {(event,newValue) => this.setState({username:newValue})}
                        />
                        <br/>
                        <TextField
                            type="password"
                            hintText="Enter your Password"
                            floatingLabelText="Password"
                            onChange = {(event,newValue) => this.setState({password:newValue})}
                        />
                        <br/>
                        <RaisedButton label="Log In" primary={true} style={style} onClick={(event) => this.loginClicked(event)}/>
                    </div>
                </MuiThemeProvider>
        );
    }

    loginClicked(event) {
        AuthenticationService.executeJwtAuthentication(this.state.username, this.state.password)
            .then(response => {
                AuthenticationService.registerSuccessfullLoginJwt(this.state.username, response.data.token);
                this.props.history.push(`/main/${this.state.username}`);
            })
            .catch(error => {
                this.setState({isLoggedIn: false});
                console
            })
    }
}
const style = {
    margin: 15,
};
export default Login;
